/**
 * Testing Multiprocess Concurrency.
 * myfopen 이후 프로세스를 생성하지 않고, fork() 등을 이용해 프로세스를 생성하는 부분 이후에 myfopen 을 통해 파일을 열도록 평가할 것입니다.
 * 
 * You may assume that test cases for grading will always be fork() -> myfopen(), not myfopen() -> fork().
*/
#include "../mystdio.h"
#include <stdio.h>
#include <assert.h>
#include <sys/wait.h>
int main() {
    pid_t pid = fork(), pid2, d=0;
    int fsize=0, i, r;
    char c, prevc, forbidden[3];
    // Lengths: 1101 each.
    // Just Initialize
    myFILE* f = myfopen("output7.txt", "w");
    myfclose(f);
    char ctext[] = "\n";
    char gtext[] = "\n";
    char ptext[] = "\n";
    if(pid==0) { // child
        f = myfopen("output7.txt", "a");
        myfwrite(ctext, 1, strlen(ctext), f);
        //myfflush(f);
        myfclose(f);
    }
    else if((pid2=fork())==0) {
        f = myfopen("output7.txt", "a");
        myfwrite(ptext, 1, strlen(ptext), f);
        //myfflush(f);
        myfclose(f);
    }
    else {
        f = myfopen("output7.txt", "a");
        myfwrite(gtext, 1, strlen(gtext), f);
        //myfflush(f);
        myfclose(f);
        d=1;
    }
    if(d==1) {
        wait(&pid);
        wait(&pid2);
        f = myfopen("output7.txt", "r");
        myfread(&prevc, sizeof(char), 1, f);
        forbidden[fsize++] = prevc;
        while(myfread(&c, sizeof(char), 1, f) > 0) {
            if(c=='\n') continue;
            r=1;
            for(i=0;i<fsize;++i) if(forbidden[i] == c) {
                assert(c==prevc);
                r=0;
                break;
            }
            if(r) {
                forbidden[fsize++]=c;
            }
            prevc=c;
        }
        myfclose(f);
        puts("Test7 Passed!");
    }
    
    return 0;
}