#include "../mystdio.h"
#include <stdio.h>
#include <assert.h>

int main() {
    pid_t pid = fork(), d=0;
    int fsize=0, i, r;
    char c, prevc, forbidden[3];
    // Lengths: 1101 each.
    // Just Initialize
    myFILE* f = myfopen("output7.txt", "w");
    myfclose(f);
    char ctext[] = "\n";
    char gtext[] = "\n";
    char ptext[] = "\n";
    f = myfopen("output7.txt", "a");
    if(pid==0) { // child
        myfwrite(ctext, 1, strlen(ctext), f);
    }
    else if((pid=fork())==0) {
        myfwrite(ptext, 1, strlen(ptext), f);
    }
    else {
        myfwrite(gtext, 1, strlen(gtext), f);
    }
    if(++d==3) {
        myfflush(f);
        myfclose(f);
        f = myfopen("output7.txt", "r");
        myfread(&prevc, sizeof(char), 1, f);
        forbidden[fsize++] = prevc;
        while(myfread(&c, sizeof(char), 1, f) > 0) {
            if(c=='\n') continue;
            r=1;
            for(i=0;i<fsize;++i) if(forbidden[i] == c) {
                assert(c==prevc);
                r=0;
                break;
            }
            if(r) {
                forbidden[fsize++]=c;
            }
            prevc=c;
        }
        myfclose(f);
        puts("Test7 Passed!");
    }
    
    return 0;
}